function apply_raty(){$(".star").raty({score:function(){return $(this).attr("data-rating")},number:function(){return $(this).attr("data-star-count")},click:function(t){$.ajax({type:"POST",beforeSend:function(t){t.setRequestHeader("X-CSRF-Token",$('meta[name="csrf-token"]').attr("content"))},url:$(this).attr("data-url"),data:{value:t,comment_id:$(this).attr("data-id"),class_name:$(this).attr("data-classname")},dataType:"script"})}})}$.fn.raty.defaults.path="/assets",$.fn.raty.defaults.half_show=!0,$(function(){apply_raty()});