function apply_raty(){$(".star").raty({score:function(){return $(this).attr("data-rating")},number:function(){return $(this).attr("data-star-count")},click:function(t){post_score(t,$(this).attr("data-id"),$(this).attr("data-classname"),$(this).attr("data-url"))}})}function post_score(t,a,e,r){$.ajax({type:"POST",beforeSend:function(t){t.setRequestHeader("X-CSRF-Token",$('meta[name="csrf-token"]').attr("content"))},url:r,data:{value:t,comment_id:a,class_name:e},dataType:"script"})}$.fn.raty.defaults.path="/assets",$.fn.raty.defaults.half_show=!0,old_id="",old_score="",$(function(){apply_raty()}),$(".rate_thumb").live("click",function(){var t=$(this).parent().attr("data-value"),a=$(this).parent().parent("div").attr("data-id");post_score(t,a,$(this).parent().parent("div").attr("data-classname"),$(this).parent().parent("div").attr("data-url")),"1"==old_score&&old_id==a&&$(this).parent().parent("div").children(".down").text(parseInt($(this).parent().parent("div").children(".down").text())-1),"2"==old_score&&old_id==a&&$(this).parent().parent("div").children(".up").text(parseInt($(this).parent().parent("div").children(".up").text())-1),old_score=t,old_id=$(this).parent().parent("div").attr("data-id"),$(this).parent().next("span").text(parseInt($(this).parent().next("span").text())+1)});